import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { Competitor} from 'src/app/models/Competitor';
import { CompetitorService } from 'src/app/services/competitor.service';

@Component({
  selector: 'app-crear-competidor',
  templateUrl: './crear-competidor.component.html',
  styleUrls: ['./crear-competidor.component.css']
})
export class CrearCompetidorComponent {
  competitorForm: FormGroup;
  titulo = 'Crear Competidor';
  id: string | null;
  constructor(private fb: FormBuilder,
              private router: Router,
              private toastr: ToastrService,
              private competitorService:CompetitorService,
              private aRouter: ActivatedRoute) { 
    this.competitorForm = this.fb.group({
      id:['',Validators.required],
      name: ['', Validators.required],
      surName: ['', Validators.required],
      birthdate: ['', Validators.required],
      discipline: ['', Validators.required],
    })
    this.id = this.aRouter.snapshot.paramMap.get('id');
  }

  ngOnInit(): void {
    this.esEditar();
  }
  agregarProducto() {
    const competitor:Competitor = {
      id: this.competitorForm.get('id')?.value,
      name: this.competitorForm.get('name')?.value,
      surName: this.competitorForm.get('surName')?.value,
      birthdate: this.competitorForm.get('birthdate')?.value,
      discipline:this.competitorForm.get(discipline)?.value,
     stakes:[]
    };

    if (this.id !== null) {
      // Modo de edición
      this.actualizarDisciplina(disciplina);
    } else {
      // Modo de creación
      this.guardarDisciplina(disciplina);
    }
  }
  guardarDisciplina(disciplina: Disciplina) {
    this.disciplinaService.saveDiscipline(disciplina).subscribe(
      data => {
        this.toastr.success('El producto fue registrado con éxito!', 'Producto Registrado!');
        this.router.navigate(['/mostrarDiscipline']);
      },
      error => {
        console.log(error);
        this.disciplineForm.reset();
      }
    );
  }
  private actualizarDisciplina(disciplina: Disciplina) {
    this.disciplinaService.update(this.id!, disciplina).subscribe(
      response => {
        console.log(response);
        this.toastr.success('La disciplina fue actualizada con éxito!', 'Disciplina Actualizada');
        this.router.navigate(['/mostrarDiscipline']);
      },
      error => {
        console.log(error);
        this.disciplineForm.reset();
      }
    );
  }
  esEditar() {
    if (this.id !== null) {
      this.titulo = 'Editar disciplina';

      this.disciplinaService.getDisciplineById(this.id).subscribe(
        response => {
          console.log(response);
          const data = response.data;
          console.log(data)
          this.disciplineForm.setValue({
            id:data.id,
            name: data.name,
            description: data.description,
            group: data.group,
          });
          this.disciplineForm.get('id')?.disable();
        },
        error => {
          console.log(error);
        });
    }
  }

}
