import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Disciplina } from '../models/Disciplina';

@Injectable({
  providedIn: 'root'
})
export class DisciplinaService {
  private url='http://localhost:2500/discipline/'

  constructor(private http:HttpClient) { }

  getDiscipline():Observable<any>{
      return this.http.get(this.url);
  }
  eliminarDiscipline(id: string): Observable<any> {
    return this.http.delete(this.url + id);
  }

  saveDiscipline(disciplina:Disciplina): Observable<any> {
    return this.http.post(this.url, disciplina);
  }

  getDisciplineById(id: string): Observable<any> {
    return this.http.get(this.url + id);
  }
}
