import { Component,OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { PuntoF } from 'src/app/models/PuntoF';
import { PuntoFService } from 'src/app/services/puntoFService';

@Component({
  selector: 'app-punto-fijo',
  templateUrl: './punto-fijo.component.html',
  styleUrls: ['./punto-fijo.component.css']
})
export class PuntoFijoComponent implements OnInit{
  eventForm: FormGroup;
  titulo='Metodo Punto Fijo';
  constructor(private fb: FormBuilder,
              private puntoService:PuntoFService
  ){
    this.eventForm = this.fb.group({
      funcion:['',Validators.required],
      puntoI:['',Validators.required],
    })
  }
  ngOnInit():void{}
  consulta() {
    const puntoF:PuntoF = {
      funcion:this.eventForm.get('funcion')?.value,
      puntoI:this.eventForm.get('puntoI')?.value
    };

      this.puntoService.save(puntoF).subscribe(
        response =>{

        },
        error => {
          console.log(error);
          this.eventForm.reset();
        }
      );

  }
}
<div class="row">
  <div class="col-sm-6">
    <div class="card">
      <div class="card-body">
        <h5 class="card-title">Special title treatment</h5>
        <p class="card-text">With supporting text below as a natural lead-in to additional content.</p>
        <a href="#" class="btn btn-primary">Go somewhere</a>
      </div>
    </div>
  </div>
  <div class="col-sm-6">
    <div class="card">
      <div class="card-body">
        <h5 class="card-title">Special title treatment</h5>
        <p class="card-text">With supporting text below as a natural lead-in to additional content.</p>
        <a href="#" class="btn btn-primary">Go somewhere</a>
      </div>
    </div>
  </div>
</div>
<div class="container mt-5">
  <div class="row">
      <div class="col-lg-6 offset-lg-3">
          <div class="card">
              <div class="card-body text-center">
                  <span class="titulo">{{titulo}}</span>
                  <form class="mt-3" [formGroup]="eventForm" (ngSubmit)="consulta()">
                      <div class="mb-3">
                          <input type="text" formControlName="funcion" class="form-control form-control-lg"
                              placeholder="Funcion">
                          <div class="text-danger"
                              *ngIf="eventForm.get('funcion')?.hasError('required') && eventForm.get('funcion')?.touched">
                              <span>La funcion <strong>es obligatoria</strong></span>
                          </div>
                      </div>
                      <div class="mb-3">
                          <input type="number" formControlName="puntoI" class="form-control form-control-lg"
                              placeholder="Punto Inicial" type="number">
                          <div class="text-danger"
                              *ngIf="eventForm.get('puntoI')?.hasError('required') && eventForm.get('puntoI')?.touched">
                              <span>El punto de inicio  <strong>es obligatorio</strong></span>
                          </div>
                      </div>



                      <div class="mb-3">
                          <button routerLink="/**" class="btn btn-secondary btn-lg float-start">VOLVER</button>
                          <button type="submit" [disabled]="eventForm.invalid" class="btn btn-success btn-lg float-end">ACEPTAR</button>
                      </div>
                  </form>
              </div>
          </div>
      </div>
  </div>
</div>
