import { PuntoF } from 'src/app/models/PuntoF';

@Component({
  selector: 'app-punto-fijo',
  templateUrl: './punto-fijo.component.html',
  styleUrls: ['./punto-fijo.component.css']
})
export class PuntoFijoComponent implements OnInit{
  constructor(){

  }
  ngOnInit():void{}
}
<div class="container">
  <form>
    <div class="form-group">
      <label for="expression">Introduce la función g(x):</label>
      <input type="text" id="expression" name="expression" class="form-control" required>
    </div>
    <div class="form-group">
      <label for="x0">Punto inicial (x0):</label>
      <input type="number" id="x0"  name="x0" class="form-control" required>
    </div>
    <button type="submit" class="btn btn-primary">Enviar</button>
  </form>

  <div >
    <h3>Resultado:</h3>
    <p>Raíz: </p>
    <h4>Iteraciones:</h4>
    <ul>
      <li *ngFor="let iteracion of ">
        Iteración {{ iteracion.iteracion }}: {{ iteracion.xi }}
      </li>
    </ul>
  </div>
</div>
