import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { Competitor} from 'src/app/models/Competitor';
import { CompetitorService } from 'src/app/services/competitor.service';
import { DisciplinaService } from 'src/app/services/disciplina.service';

@Component({
  selector: 'app-crear-competidor',
  templateUrl: './crear-competidor.component.html',
  styleUrls: ['./crear-competidor.component.css']
})
export class CrearCompetidorComponent {
  disciplinas:[]
  competitorForm: FormGroup;
  titulo = 'Crear Competidor';
  id: string | null;
  constructor(private fb: FormBuilder,
              private router: Router,
              private toastr: ToastrService,
              private competitorService:CompetitorService,
              private disciplinaService:DisciplinaService,
              private aRouter: ActivatedRoute) { 
    this.competitorForm = this.fb.group({
      id:['',Validators.required],
      name: ['', Validators.required],
      surName: ['', Validators.required],
      birthdate: ['', Validators.required],
      discipline: ['', Validators.required],
    })
    this.id = this.aRouter.snapshot.paramMap.get('id');
  }

  ngOnInit(): void {
    this.esEditar();
  }
  agregarProducto() {
    const competitor:Competitor = {
      id: this.competitorForm.get('id')?.value,
      name: this.competitorForm.get('name')?.value,
      surName: this.competitorForm.get('surName')?.value,
      birthdate: this.competitorForm.get('birthdate')?.value,
      discipline:this.competitorForm.get('discipline')?.value,
     stakes:[]
    };

    if (this.id !== null) {
      // Modo de edición
      this.actualizarCompetitor(competitor);
    } else {
      // Modo de creación
      this.guardarCompetitor(competitor,this.competitorForm.get('discipline')?.value);
    }
  }
  guardarCompetitor(competitor: Competitor,id:String) {
    this.competitorService.saveCompetitor(competitor,id).subscribe(
      data => {
        this.toastr.success('El Competidor fue registrado con éxito!', 'Producto Registrado!');
        this.router.navigate(['/mostrarCompetitor']);
      },
      error => {
        console.log(error);
        this.competitorForm.reset();
      }
    );
  }
  private actualizarCompetitor(competitor: Competitor) {
    this.competitorService.update(this.id!, competitor).subscribe(
      response => {
        console.log(response);
        this.toastr.success('El competidor fue actualizada con éxito!', 'Disciplina Actualizada');
        this.router.navigate(['/mostrarCompetitor']);
      },
      error => {
        console.log(error);
        this.competitorForm.reset();
      }
    );
  }
  esEditar() {
    if (this.id !== null) {
      this.titulo = 'Editar Competidor';

      this.competitorService.getCompetitorById(this.id).subscribe(
        response => {
          console.log(response);
          const data = response.data;
          console.log(data)
          this.competitorForm.setValue({
            id:data.id,
            name: data.name,
            surName: data.surName,
            birthdate:data.birthdate,
            discipline:data.discipline
          });
          this.competitorForm.get('id')?.disable();
        },
        error => {
          console.log(error);
        });
    }
  }
  obtenerDisciplinas() {
    this.disciplinaService.getDiscipline().subscribe(
      response => {
        console.log(response);
        const data = response.data;
        if (Array.isArray(data)) {
          this.listaDisciplina = data;
          for (const iterator of this.listaDisciplina) {
            console.log(iterator.name);
          }
        } else {
          console.error('Los datos recibidos no son un array:', data);
        }
      },
      error => {
        console.log(error);
      }
    );
  }

}
<div class="container mt-5">
    <div class="row">
        <div class="col-lg-6 offset-lg-3">
            <div class="card">
                <div class="card-body text-center">
                    <span class="titulo">{{titulo}}</span>
                    <form class="mt-3" [formGroup]="competitorForm" (ngSubmit)="agregarProducto()">
                        <div class="mb-3">
                            <input type="text" formControlName="id" class="form-control form-control-lg"
                                placeholder="Id">
                            <div class="text-danger"
                                *ngIf="competitorForm.get('id')?.hasError('required') && competitorForm.get('id')?.touched">
                                <span>El Id de la disciplina <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <input type="text" formControlName="name" class="form-control form-control-lg"
                                placeholder="Nombre">
                            <div class="text-danger"
                                *ngIf="competitorForm.get('name')?.hasError('required') && competitorForm.get('name')?.touched">
                                <span>El nombre de la disciplina <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <input type="text" formControlName="surName" class="form-control form-control-lg"
                                placeholder="Apellido">
                            <div class="text-danger"
                                *ngIf="competitorForm.get('surName')?.hasError('required') && competitorForm.get('surName')?.touched">
                                <span>El apellido  <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <input type="text" formControlName="birthdate" class="form-control form-control-lg"
                                placeholder="Fecha_Nacimiento" type="date">
                            <div class="text-danger"
                                *ngIf="competitorForm.get('birthdate')?.hasError('required') && competitorForm.get('birthdate')?.touched">
                                <span>El apellido  <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <select formControlName="discipline" class="form-control form-control-lg">
                                <option value="">Seleccione una Disciplina</option>
                                <option value="true"></option>
                                <option value="false">Individual</option>
                            </select>
                            <div class="text-danger"
                                *ngIf="competitorForm.get('discipline')?.hasError('required') && competitorForm.get('discipline')?.touched">
                                <span>El tipo <strong>es obligatorio</strong></span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <button routerLink="/mostrarCompetitor" class="btn btn-secondary btn-lg float-start">VOLVER</button>
                            <button type="submit" [disabled]="competitorForm.invalid" class="btn btn-success btn-lg float-end">ACEPTAR</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>
