import { Component } from '@angular/core';
import {Event} from 'src/app/models/Event';
import { ToastrService } from 'ngx-toastr';
import { EventService } from 'src/app/services/event.service';

@Component({
  selector: 'app-mostrar-event',
  templateUrl: './mostrar-event.component.html',
  styleUrls: ['./mostrar-event.component.css']
})
export class MostrarEventComponent {
  listaEvent : Event[]=[];
  constructor(private toastr: ToastrService,
    private eventService:EventService){

  }
  ngOnInit(){
this.obtenerEvents();
  }
  obtenerEvents() {
    this.eventService.getEvent().subscribe(
      response => {
        console.log(response);
        const data = response.data;
        if (Array.isArray(data)) {
          this.listaEvent = data;
          for (const iterator of this.listaEvent) {
            console.log(iterator.name);
          }
        } else {
          console.error('Los datos recibidos no son un array:', data);
        }
      },
      error => {
        console.log(error);
      }
    );
  }
  eliminarEvent(id: any) {
    this.eventService.eliminarEvent(id).subscribe( response => {
      console.log(response);
        const data = response.data;
      console.log('El producto fue eliminado con exito');
        this.toastr.error('El producto fue eliminado con exito' ,'Producto Eliminado');
        this.obtenerEvents();

    }, error => {
      console.log(error);
    })
  }
}
<div class="container mt-5">
    <div class="row">
        <div class="col-lg-8 offset-lg-2">
            <div class="card">
                <div class="card-body text-center">
                    <span class="titulo">LISTADO DE E</span>
                    <button class="btn btn-success float-end" routerLink="/saveEvent">NUEVO</button>
                    <table *ngIf="listaEvent.length > 0" class="table table-striped mt-3">
                        <thead>
                          <tr>
                            <th scope="col">Id</th>
                            <th scope="col">Nombre</th>
                            <th scope="col">Fecha_Inicio</th>
                            <th scope="col">Fecha_Fin</th>
                            <th scope="col">Acciones</th>
                          </tr>
                        </thead>
                        <tbody>
                          <tr *ngFor="let event of listaEvent">
                            <td>{{ event.id }}</td>
                            <td>{{ event.name }}</td>
                            <td>{{ event.startDate }}</td>
                            <td>{{ event.endingDate }}</td>
                            <td>
                                <i [routerLink]="['/updateEvent', event.id]" class="fas fa-edit text-primary"></i>
                                <i (click)="eliminarEvent(event.id)" class="fas fa-trash text-danger"></i>
                            </td>
                          </tr>
                        </tbody>
                      </table>
                      <h5 style="text-align: start; margin-top: 10px;" *ngIf="listaEvent.length == 0">No hay Eventos para mostrar</h5>
                </div>
            </div>
        </div>
    </div>
</div>
