import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { Disciplina } from 'src/app/models/Disciplina';
import { DisciplinaService } from 'src/app/services/disciplina.service';

@Component({
  selector: 'app-creardisciplina',
  templateUrl: './creardisciplina.component.html',
  styleUrls: ['./creardisciplina.component.css']
})
export class CreardisciplinaComponent {
  disciplineForm: FormGroup;
  titulo = 'Crear producto';
  id: string | null;
  constructor(private fb: FormBuilder,
              private router: Router,
              private toastr: ToastrService,
              private disciplinaService:DisciplinaService,
              private aRouter: ActivatedRoute) { 
    this.disciplineForm = this.fb.group({
      id:['',Validators.required],
      name: ['', Validators.required],
      description: ['', Validators.required],
      group: ['', Validators.required],
    })
    this.id = this.aRouter.snapshot.paramMap.get('id');
  }

  ngOnInit(): void {
    this.esEditar();
  }

  agregarProducto() {

    const DISCIPLINA: Disciplina= {
      id: this.disciplineForm.get('id')?.value,
      name: this.disciplineForm.get('name')?.value,
      description: this.disciplineForm.get('description')?.value,
      group: this.disciplineForm.get('group')?.value,
      competitors:[]
    }

    console.log(DISCIPLINA);
    this.disciplinaService.saveDiscipline(DISCIPLINA).subscribe(data => {
      this.toastr.success('El producto fue registrado con exito!', 'Producto Registrado!');
      this.router.navigate(['/']);
    }, error => {
      console.log(error);
      this.disciplineForm.reset();
    })


  }

  esEditar() {

    if(this.id !== null) {
      this.titulo = 'Editar producto';
      this.disciplinaService.getDisciplineById(this.id).subscribe(data => {
        this.disciplineForm.setValue({
          name: data.name,
          descrition: data.description,
          group: data.group,
        })
      })
    }
  }


}
