import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
//import { ToastrService } from 'ngx-toastr';
import { Disciplina } from 'src/app/models/Disciplina';
import { DisciplinaService } from 'src/app/services/disciplina.service';

@Component({
  selector: 'app-creardisciplina',
  templateUrl: './creardisciplina.component.html',
  styleUrls: ['./creardisciplina.component.css']
})
export class CreardisciplinaComponent {
  disciplineForm: FormGroup;
  titulo = 'Crear producto';
  id: string | null;
  constructor(private fb: FormBuilder,
              private router: Router,
              //private toastr: ToastrService,
              private disciplinaService:DisciplinaService,
              private aRouter: ActivatedRoute) { 
    this.disciplineForm = this.fb.group({
      name: ['', Validators.required],
      description: ['', Validators.required],
      group: ['', Validators.required],
    })
    this.id = this.aRouter.snapshot.paramMap.get('id');
  }

  ngOnInit(): void {
    this.esEditar();
  }

  agregarProducto() {

    const DISCIPLINA: Disciplina= {
      name: this.disciplineForm.get('name')?.value,
      description: this.disciplineForm.get('descript')?.value,
      ubicacion: this.productoForm.get('ubicacion')?.value,
      precio: this.productoForm.get('precio')?.value,
    }

    console.log(PRODUCTO);
    this._productoService.guardarProducto(PRODUCTO).subscribe(data => {
      this.toastr.success('El producto fue registrado con exito!', 'Producto Registrado!');
      this.router.navigate(['/']);
    }, error => {
      console.log(error);
      this.productoForm.reset();
    })


  }

  esEditar() {

    if(this.id !== null) {
      this.titulo = 'Editar producto';
      this._productoService.obtenerProducto(this.id).subscribe(data => {
        this.productoForm.setValue({
          producto: data.nombre,
          categoria: data.categoria,
          ubicacion: data.ubicacion,
          precio: data.precio,
        })
      })
    }
  }


}
<div class="container mt-5">
    <div class="row">
        <div class="col-lg-6 offset-lg-3">
            <div class="card">
                <div class="card-body text-center">
                    <span class="titulo">CREAR DISCIPLINA</span>
                    <form class="mt-3" [formGroup]="disciplineForm" (ngSubmit)="agregarProducto()">
                        <div class="mb-3">
                            <input type="text" formControlName="name" class="form-control form-control-lg"
                                placeholder="Nombre">
                            <div class="text-danger"
                                *ngIf="disciplineForm.get('name')?.hasError('required') && disciplineForm.get('name')?.touched">
                                <span>El nombre del producto <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <input type="text" formControlName="description" class="form-control form-control-lg"
                                placeholder="Descripcion">
                            <div class="text-danger"
                                *ngIf="disciplineForm.get('description')?.hasError('required') && disciplineForm.get('description')?.touched">
                                <span>La Descripcion <strong>es obligatoria</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <input type="text" formControlName="group" class="form-control form-control-lg"
                                placeholder="Tipo">
                            <div class="text-danger"
                                *ngIf="disciplineForm.get('group')?.hasError('required') && disciplineForm.get('group')?.touched">
                                <span>El tipo <strong>es obligatorio</strong></span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <button routerLink="/mostrarDiscipline" class="btn btn-secondary btn-lg float-start">VOLVER</button>
                            <button type="submit" [disabled]="disciplineForm.invalid" class="btn btn-success btn-lg float-end">ACEPTAR</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>
