import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { Event} from 'src/app/models/Event';
import { Disciplina} from 'src/app/models/Disciplina';
import { EventService } from 'src/app/services/event.service';
import { DisciplinaService } from 'src/app/services/disciplina.service';

@Component({
  selector: 'app-crear-event',
  templateUrl: './crear-event.component.html',
  styleUrls: ['./crear-event.component.css']
})
export class CrearEventComponent {
  eventForm: FormGroup;
  titulo = 'Crear Evento';
  id: string | null;
  constructor(private fb: FormBuilder,
              private router: Router,
              private toastr: ToastrService,
              private eventService:EventService,
              private disciplinaService:DisciplinaService,
              private aRouter: ActivatedRoute) { 
    this.eventForm = this.fb.group({
      id:['',Validators.required],
      name: ['', Validators.required],
      startDate: ['', Validators.required],
      endingDate: ['', Validators.required],
    })
    this.id = this.aRouter.snapshot.paramMap.get('id');
  }

  ngOnInit(): void {
    this.esEditar();
  }
  agregarProducto() {
    const event:Event = {
      id: this.eventForm.get('id')?.value,
      name: this.eventForm.get('name')?.value,
      startDate: this.eventForm.get('startDate')?.value,
      endingDate: this.eventForm.get('endingDate')?.value,
     stakes:[]
    };

    if (this.id !== null) {
      // Modo de edición
      this.actualizarEvent(event);
    } else {
      // Modo de creación
      this.guardarEvent(event);
    }
  }
  guardarEvent(event:Event) {
    this.eventService.saveEvent(event).subscribe(
      data => {
        this.toastr.success('El Evento fue registrado con éxito!', 'Producto Registrado!');
        this.router.navigate(['/mostrarEvent']);
      },
      error => {
        console.log(error);
        this.eventForm.reset();
      }
    );
  }
  private actualizarEvent(event: Event) {
    this.eventService.update(this.id!, event).subscribe(
      response => {
        console.log(response);
        this.toastr.success('El evento fue actualizada con éxito!', 'Disciplina Actualizada');
        this.router.navigate(['/mostrarEvent']);
      },
      error => {
        console.log(error);
        this.eventForm.reset();
      }
    );
  }
  esEditar() {
    if (this.id !== null) {
      this.titulo = 'Editar evento';

      this.eventService.getEventById(this.id).subscribe(
        response => {
          console.log(response);
          const data = response.data;
          console.log(data)
          this.eventForm.setValue({
            id:data.id,
            name: data.name,
            startDate: data.startDate,
            endingDate:data.endingDate
          });
          this.eventForm.get('id')?.disable();
        },
        error => {
          console.log(error);
        });
    }
  }



}
<div class="container mt-5">
    <div class="row">
        <div class="col-lg-6 offset-lg-3">
            <div class="card">
                <div class="card-body text-center">
                    <span class="titulo">{{titulo}}</span>
                    <form class="mt-3" [formGroup]="eventForm" (ngSubmit)="agregarProducto()">
                        <div class="mb-3">
                            <input type="text" formControlName="id" class="form-control form-control-lg"
                                placeholder="Id">
                            <div class="text-danger"
                                *ngIf="eventForm.get('id')?.hasError('required') && eventForm.get('id')?.touched">
                                <span>El Id de la disciplina <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <input type="text" formControlName="name" class="form-control form-control-lg"
                                placeholder="Nombre">
                            <div class="text-danger"
                                *ngIf="competitorForm.get('name')?.hasError('required') && competitorForm.get('name')?.touched">
                                <span>El nombre de la disciplina <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <input type="text" formControlName="surName" class="form-control form-control-lg"
                                placeholder="Apellido">
                            <div class="text-danger"
                                *ngIf="competitorForm.get('surName')?.hasError('required') && competitorForm.get('surName')?.touched">
                                <span>El apellido  <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <input type="text" formControlName="birthdate" class="form-control form-control-lg"
                                placeholder="Fecha_Nacimiento" type="date">
                            <div class="text-danger"
                                *ngIf="competitorForm.get('birthdate')?.hasError('required') && competitorForm.get('birthdate')?.touched">
                                <span>El apellido  <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <select formControlName="discipline" class="form-control form-control-lg">
                                <option value="">Seleccione una Disciplina</option>
                                <option *ngFor="let disciplina of disciplinas" value={{disciplina.id}}>{{ disciplina.name }}</option>
                            </select>
                            <div class="text-danger"
                                *ngIf="competitorForm.get('discipline')?.hasError('required') && competitorForm.get('discipline')?.touched">
                                <span>La disciplina<strong>es obligatoria</strong></span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <button routerLink="/mostrarCompetitor" class="btn btn-secondary btn-lg float-start">VOLVER</button>
                            <button type="submit" [disabled]="competitorForm.invalid" class="btn btn-success btn-lg float-end">ACEPTAR</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>
