import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { Event} from 'src/app/models/Event';
import { Disciplina} from 'src/app/models/Disciplina';
import { EventService } from 'src/app/services/event.service';
import { DisciplinaService } from 'src/app/services/disciplina.service';

@Component({
  selector: 'app-crear-event',
  templateUrl: './crear-event.component.html',
  styleUrls: ['./crear-event.component.css']
})
export class CrearEventComponent {
  competitorForm: FormGroup;
  titulo = 'Crear Evento';
  id: string | null;
  constructor(private fb: FormBuilder,
              private router: Router,
              private toastr: ToastrService,
              private eventService:EventService,
              private disciplinaService:DisciplinaService,
              private aRouter: ActivatedRoute) { 
    this.competitorForm = this.fb.group({
      id:['',Validators.required],
      name: ['', Validators.required],
      startDate: ['', Validators.required],
      endingDate: ['', Validators.required],
    })
    this.id = this.aRouter.snapshot.paramMap.get('id');
  }

  ngOnInit(): void {
    this.obtenerDisciplinas();
    this.esEditar();
  }
  agregarProducto() {
    const competitor:Competitor = {
      id: this.competitorForm.get('id')?.value,
      name: this.competitorForm.get('name')?.value,
      surName: this.competitorForm.get('surName')?.value,
      birthdate: this.competitorForm.get('birthdate')?.value,
      discipline:this.competitorForm.get('discipline')?.value,
     stakes:[]
    };

    if (this.id !== null) {
      // Modo de edición
      this.actualizarCompetitor(competitor);
    } else {
      // Modo de creación
      this.guardarCompetitor(competitor,this.competitorForm.get('discipline')?.value);
    }
  }
  guardarCompetitor(competitor: Competitor,id:String) {
    this.competitorService.saveCompetitor(competitor,id).subscribe(
      data => {
        this.toastr.success('El Competidor fue registrado con éxito!', 'Producto Registrado!');
        this.router.navigate(['/mostrarCompetitor']);
      },
      error => {
        console.log(error);
        this.competitorForm.reset();
      }
    );
  }
  private actualizarCompetitor(competitor: Competitor) {
    this.competitorService.update(this.id!, competitor).subscribe(
      response => {
        console.log(response);
        this.toastr.success('El competidor fue actualizada con éxito!', 'Disciplina Actualizada');
        this.router.navigate(['/mostrarCompetitor']);
      },
      error => {
        console.log(error);
        this.competitorForm.reset();
      }
    );
  }
  esEditar() {
    if (this.id !== null) {
      this.titulo = 'Editar Competidor';

      this.competitorService.getCompetitorById(this.id).subscribe(
        response => {
          console.log(response);
          const data = response.data;
          console.log(data)
          this.competitorForm.setValue({
            id:data.id,
            name: data.name,
            surName: data.surName,
            birthdate:data.birthdate,
            discipline:data.discipline
          });
          this.competitorForm.get('id')?.disable();
          this.competitorForm.get('discipline')?.disable();
        },
        error => {
          console.log(error);
        });
    }
  }



}
