import { Component } from '@angular/core';
import { Disciplina } from 'src/app/models/Disciplina';
import { DisciplinaService } from 'src/app/services/disciplina.service';

@Component({
  selector: 'app-mostrardisciplina',
  templateUrl: './mostrardisciplina.component.html',
  styleUrls: ['./mostrardisciplina.component.css']
})
export class MostrardisciplinaComponent {
  listaDisciplina : Disciplina[]=[];
  constructor(private disciplinaService:DisciplinaService){}
  ngOnInit(){
this.obtenerDisciplinas();
  }
  obtenerDisciplinas() {
    this.disciplinaService.getDiscipline().subscribe(
      response => {
        console.log(response);
        const data = response.data;
        if (Array.isArray(data)) {
          this.listaDisciplina = data;
          for (const iterator of this.listaDisciplina) {
            console.log(iterator.name);
          }
        } else {
          console.error('Los datos recibidos no son un array:', data);
        }
      },
      error => {
        console.log(error);
      }
    );
  }

}
