import { Component } from '@angular/core';
import {Competitor} from 'src/app/models/Competitor';
import { ToastrService } from 'ngx-toastr';
import { DisciplinaService } from 'src/app/services/disciplina.service';
import { CompetitorService } from 'src/app/services/competitor.service';

@Component({
  selector: 'app-mostrar-competidor',
  templateUrl: './mostrar-competidor.component.html',
  styleUrls: ['./mostrar-competidor.component.css']
})
export class MostrarCompetidorComponent {
  listaCompetitor : Competitor[]=[];
  disciplinasMap: { [id: string]: string } = {};
  constructor(private toastr: ToastrService,
    private competitorService:CompetitorService,
    private disciplinaService:DisciplinaService){

  }
  ngOnInit(){
this.obtenerCompetidores();
  }
  obtenerCompetidores() {
    this.competitorService.getCompetitor().subscribe(
      response => {
        console.log(response);
        const data = response.data;
        if (Array.isArray(data)) {
          this.listaCompetitor = data;
          for (const iterator of this.listaCompetitor) {
            console.log(iterator.name);
          }
        } else {
          console.error('Los datos recibidos no son un array:', data);
        }
      },
      error => {
        console.log(error);
      }
    );
  }
  eliminarCompetidor(id: any) {
    this.competitorService.eliminarCompetitor(id).subscribe( response => {
      console.log(response);
        const data = response.data;
      console.log('El producto fue eliminado con exito');
        this.toastr.error('El producto fue eliminado con exito' ,'Producto Eliminado');
        this.obtenerCompetidores();

    }, error => {
      console.log(error);
    })
  }
  buscarDisciplina(id: string) {
    this.disciplinaService.getDisciplineByIdO(id).subscribe(
      response => {
        console.log(response);
        const data = response.data;
        this.disciplinasMap[id] = data.name;
      },
      error => {
        console.log(error);
      }
    );
  }
}
