import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { Competitor} from 'src/app/models/Competitor';
import { Event} from 'src/app/models/Event';
import {Stake} from 'src/app/models/Stake';
import { CompetitorService } from 'src/app/services/competitor.service';
import { EventService } from 'src/app/services/event.service';
import { StakesService } from 'src/app/services/stakes.service';


@Component({
  selector: 'app-crear-stake',
  templateUrl: './crear-stake.component.html',
  styleUrls: ['./crear-stake.component.css']
})
export class CrearStakeComponent {
  competidores:Competitor[]=[];
  eventos:Event[]=[];
  stakeForm: FormGroup;
  titulo = 'Crear Participacion';
  id: string | null;
  constructor(private fb: FormBuilder,
              private router: Router,
              private toastr: ToastrService,
              private competitorService:CompetitorService,
              private eventService:EventService,
              private stakeService:StakesService,
              private aRouter: ActivatedRoute) { 
    this.stakeForm = this.fb.group({
      id:['',Validators.required],
      position: ['', Validators.required],
      competitor: ['', Validators.required],
      event: ['', Validators.required],
    })
    this.id = this.aRouter.snapshot.paramMap.get('id');
  }

  ngOnInit(): void {
    this.obtenerCompetidores();
    this.obtenerEventos();
    this.esEditar();
  }
  agregarProducto() {
    const stake:Stake = {
      id: this.stakeForm.get('id')?.value,
      position: this.stakeForm.get('position')?.value,
      competitor: this.stakeForm.get('competitor')?.value,
      event: this.stakeForm.get('event')?.value
    };

    if (this.id !== null) {
      // Modo de edición
      this.actualizarStake(stake);
    } else {
      // Modo de creación
      this.guardarStake(stake,this.stakeForm.get('competitor')?.value,this.stakeForm.get('event')?.value);
    }
  }
  guardarStake(stake:Stake,idC:String,idE:String) {
    this.stakeService.saveStake(stake,idC,idE).subscribe(
      data => {
        this.toastr.success('La participacion fue registrado con éxito!', 'Producto Registrado!');
        this.router.navigate(['/mostrarStake']);
      },
      error => {
        console.log(error);
        this.stakeForm.reset();
      }
    );
  }
  private actualizarStake(stake:Stake) {
    this.stakeService.update(this.id!, stake).subscribe(
      response => {
        console.log(response);
        this.toastr.success('La participacion fue actualizada con éxito!', 'Disciplina Actualizada');
        this.router.navigate(['/mostrarStake']);
      },
      error => {
        console.log(error);
        this.stakeForm.reset();
      }
    );
  }
  esEditar() {
    if (this.id !== null) {
      this.titulo = 'Editar Participacion';

      this.stakeService.getStakeById(this.id).subscribe(
        response => {
          console.log(response);
          const data = response.data;
          console.log(data)
          this.stakeForm.setValue({
            id:data.id,
            position : data.position,
            competitor: data.competitor,
            event:data.event
          });
          this.stakeForm.get('id')?.disable();
          this.stakeForm.get('competitor')?.disable();
          this.stakeForm.get('event')?.disable();
        },
        error => {
          console.log(error);
        });
    }
  }
  obtenerCompetidores() {
    this.competitorService.getCompetitor().subscribe(
      response => {
        console.log(response);
        const data = response.data;
        if (Array.isArray(data)) {
          this.competidores = data;
          for (const iterator of this.competidores) {
            console.log(iterator.name);
          }
        } else {
          console.error('Los datos recibidos no son un array:', data);
        }
      },
      error => {
        console.log(error);
      }
    );
  }
  obtenerEventos() {
    this.eventService.getEvent().subscribe(
      response => {
        console.log(response);
        const data = response.data;
        if (Array.isArray(data)) {
          this.eventos = data;
          for (const iterator of this.eventos) {
            console.log(iterator.name);
          }
        } else {
          console.error('Los datos recibidos no son un array:', data);
        }
      },
      error => {
        console.log(error);
      }
    );
  }

}
<div class="container mt-5">
    <div class="row">
        <div class="col-lg-6 offset-lg-3">
            <div class="card">
                <div class="card-body text-center">
                    <span class="titulo">{{titulo}}</span>
                    <form class="mt-3" [formGroup]="stakeForm" (ngSubmit)="agregarProducto()">
                        <div class="mb-3">
                            <input type="text" formControlName="id" class="form-control form-control-lg"
                                placeholder="Id">
                            <div class="text-danger"
                                *ngIf="stakeForm.get('id')?.hasError('required') && stakeForm.get('id')?.touched">
                                <span>El Id de la participacion <strong>es obligatorio</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <input type="text" formControlName="position" class="form-control form-control-lg"
                                placeholder="Posicion" type="number">
                            <div class="text-danger"
                                *ngIf="stakeForm.get('position')?.hasError('required') && stakeForm.get('position')?.touched">
                                <span>La posicion de la participacion<strong>es obligatorio</strong></span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <select formControlName="competitor" class="form-control form-control-lg">
                                <option value="">Seleccione un Competidor</option>
                                <option *ngFor="let competidor of competidores" value={{competidor.id}}>{{ competidor.name }}</option>
                            </select>
                            <div class="text-danger"
                                *ngIf="stakeForm.get('competitor')?.hasError('required') && stakeForm.get('competitor')?.touched">
                                <span>La disciplina<strong>es obligatoria</strong></span>
                            </div>
                        </div>
                        <div class="mb-3">
                            <select formControlName="event" class="form-control form-control-lg">
                                <option value="">Seleccione un Evento</option>
                                <option *ngFor="let evento of eventos" value={{event.id}}>{{ event.name }}</option>
                            </select>
                            <div class="text-danger"
                                *ngIf="stakeForm.get('competitor')?.hasError('required') && stakeForm.get('competitor')?.touched">
                                <span>La disciplina<strong>es obligatoria</strong></span>
                            </div>
                        </div>

                        <div class="mb-3">
                            <button routerLink="/mostrarCompetitor" class="btn btn-secondary btn-lg float-start">VOLVER</button>
                            <button type="submit" [disabled]="competitorForm.invalid" class="btn btn-success btn-lg float-end">ACEPTAR</button>
                        </div>
                    </form>
                </div>
            </div>
        </div>
    </div>
</div>
